void Collector::AngleUp(){
	angleAdjuster->Set(angleAdjuster->Get()+1);
}
void Collector::AngleDown(){
	angleAdjuster->SetAngle(angleAdjuster->GetAngle()-1);
}
void Collector::SetCollectorSpeed(float speed){
	leftCollect->Set((leftCollect->Get()+speed),Talon::kPeriodMultiplier_1X);
	rightCollect->Set((rightCollect->Get()+speed),Talon::kPeriodMultiplier_1X);
	printf("%f",leftCollect->Get());
}
void Collector::ZeroCollector(){
	leftCollect->Set(0.0,Talon::kPeriodMultiplier_1X);
	rightCollect->Set(0.0,Talon::kPeriodMultiplier_1X);
}
