#include "Collector.h"
#include "../Robotmap.h"
Collector::Collector() : Subsystem("Collector") {
	// BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
	elevationEncoder = RobotMap::collectorElevationEncoder;
	leftCollect = RobotMap::collectorLeftCollect;
	rightCollect = RobotMap::collectorRightCollect;
	collectorJag = RobotMap::collectorCollectorJag;
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
	realSpeed=0.00;
	angleSpeed=0.00;
}
    
void Collector::InitDefaultCommand() {
	leftCollect->Set(0.0,Talon::kPeriodMultiplier_1X);
	rightCollect->Set(0.0,Talon::kPeriodMultiplier_1X);
	collectorJag->Set(0.0);
	// BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND
    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND
}
void Collector::SetSpeed(float speed){
	realSpeed=speed;
	leftCollect->Set(realSpeed, Talon::kPeriodMultiplier_1X);
	rightCollect->Set(realSpeed,Talon::kPeriodMultiplier_1X);
}
float Collector::GetSpeed(){
	return realSpeed;
}
void Collector::SetAngleSpeed(float swag){
	angleSpeed=swag;
	collectorJag->Set(angleSpeed);
}
float Collector::GetAngleSpeed(){
	return angleSpeed;
}
void Collector::SetAngleUp(){
	collectorJag->Set(0.5);
}
void Collector::SetAngleDown(){
	collectorJag->Set(-0.1);
}
